module.exports = BaseFaction;

function BaseFaction() {
  var my = this;
  my.name = "Base";

  var spice = 0;
  var troops = [];

  assignTroops(troops);
  
  my.getTroopCount = function() {
    return troops.length;
  }

  my.getTroops = function(count) {
    return troops.slice(0,count);
  };
  
  my.getSpice = function() {
    return spice;
  };

  my.setSpice = function(value) {
    spice = value;
  }

  return my;
}

function assignTroops(troopArray) {
  for (var i = 0; i < 20; i++) {
    troopArray.push(new BaseFactionTroop());
  }
}


function BaseFactionTroop() {
  var strength = 0;
  var my = this;

  my.getStrength = function() {
    return strength;
  }
  return my;
}

