module.exports = BaseFaction;

function BaseFaction() {
  var my = this;
  my.name = "Base";

  var troops = 20;
  var spice = 0;
  

  my.getTroops = function() {
    return troops;
  };
  
  my.getSpice = function() {
    return spice;
  };

  my.setSpice = function(value) {
    spice = value;
  }

  return my;
}

