module.exports = Game;

function Game() {
  var my = this;

  my.newPlayer = function() {
    return new GenericFaction();
  };

  my.newFaction = function(factionModule) {
    var FactionClass = require("./Factions/"+factionModule);
    return new FactionClass();
  };

  my.getMap = function() {
    var ArrakisMap = require("./Map");
    return new ArrakisMap();
  }

  return my;
}

