var eventChain = require("Dune/EventChain");
module.exports = new RoundView();

/*"storm", "spiceBlow", "bidding", "revival", "movement", "battle", */
/*"collection", "control"*/
function RoundView() 
{

  this.start = function() {
    eventChain.add([
      function() { storm() },
      function() { spiceBlow() },
      function() { bidding() },
      function() { revival() }
    ]);
    eventChain.next();
  }

  function resetRounds() {

    console.log('round reset');
    rounds = new Array(
      storm, spiceBlow, bidding
    );
  }

  function storm() 
  {
    console.log('storm round');
    eventChain.next();
  }

  function spiceBlow() 
  {
    console.log('spice blow round');
    eventChain.next();
  }

  function bidding() 
  {
    console.log('bidding round');
    eventChain.next();
  }

  function revival() 
  {
    console.log('revival round');
    eventChain.next();
  }


}
