module.exports = turnScriptIntoModulino;

function turnScriptIntoModulino(runScriptDirectly, scriptModule) 
{
  if (isScriptCalledAsModule(scriptModule)) 
  {
    return;
  } 
  else 
  {
    runScriptDirectly();
  }
}

function isScriptCalledAsModule(scriptModule) 
{
  /* boolean function */
  if (scriptModule.parent) {
    return 1;
  } else {
    return 0
  }
}

